import { createSlice } from '@reduxjs/toolkit'

export const uiSlice = createSlice({
    name: 'ui',
    initialState: {
        showCart: false
    },
    reducers: {
        toggleCart: (state, action) => {
            state.showCart = !state.showCart
        }
    }
});

export const { toggleCart } = uiSlice.actions;

export default uiSlice.reducer;
