import { configureStore } from '@reduxjs/toolkit';
import cartSlice from './cart-slice';
import toggleCartSlice from './ui-slice';

export default configureStore({
    reducer: {
        cart: cartSlice,
        toggleCart: toggleCartSlice
    },
})
