import { Fragment, useEffect } from 'react';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector, useDispatch } from 'react-redux';
import { setNotification } from './store/ui-slice';
import Notification from './components/UI/Notification';

function App() {
  const showCart = useSelector((state) => state.ui.showCart)
  const cart = useSelector(state => state.cart);
  const dispatch = useDispatch();
  const notification = useSelector(state => state.ui.notification);

  /* watch for changes in the cart state*, because useEffect run side effects 
    so it will help me to run an effect whenever soome dependency changes */  

    /* PUT vs POST => PUT request will also store data to Firebase, but the new data will not be added in a list of data,
    it will OVERRIDE the EXISTING data. */
  useEffect(() => {
    const sendCartData = async () => {
      dispatch(setNotification({
          status: 'pending',
          title: 'Sending...',
          message: 'Sending cart data!'
        })
      );
      const response = await fetch('https://react-http-8fe27-default-rtdb.europe-west1.firebasedatabase.app/cart.json', {
        method: 'PUT',
        body: JSON.stringify(cart) 
      });

      if(!response.ok){
        throw new Error('Sending cart data failed');
        // dispatch(setNotification({
        //     status: 'error',
        //     title: 'Error...',
        //     message: 'Sending cart data failed'
        //   })
        // );
      }

      // const responseData = await response.json();

      dispatch(setNotification({
        status: 'success',
        title: 'Success!',
        message: 'Sent cart data succesfully!'
      })
    );
    }

    sendCartData().catch(error => {
      dispatch(setNotification({
          status: 'error',
          title: 'Error...',
          message: 'Sending cart data failed'
        })
      );
    });
  }, [cart, dispatch]);


  return (
    <Fragment>
      <Notification />
      <Layout>
      { showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
