import { addItem } from '../../store/cart-slice';
import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import { useDispatch, useSelector } from 'react-redux';

const ProductItem = (props) => {
  const cart = useSelector(state => state.cart);
  const dispatch = useDispatch();
  const { title, price, description, id } = props;

  const addToCartHanler = () => {
    const newTotalQty = cart.newTotalQty + 1;

    const updatedItems = cart.itmes.slice(); // create a copy via slice tp avoid mutating
    const existingItem = updatedItems.find((item) => item.id === id);
    if(existingItem){
      const updatedItem = { ...existingItem }; // new object + copy existing properties
      updatedItem.quantity++;
      updatedItem.price = updatedItem.price + price;
      const existingItemIndex = updatedItems.findIndex((item) => item.id === id)
      updatedItems[existingItemIndex] = updatedItem; 
    }
  }
  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>${price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={() => dispatch(addItem(
            {
              id,
              price,
              title,
              description
            }
          ))}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
