import React from "react";

class ErrorBoundary extends React.Component {
  constructor() {
    super();
    this.state = {
      hasError: false,
    };
  }

  // do something when error occurs
  componentDidCatch(error) {
    console.log(error);
  }

  render() {
    return this.props.chilren;
  }
}

export default ErrorBoundary;
