import React from "react";
import styles from "./Input.module.css";

const Input = (props) => {
  return (
    <div
      className={`${styles.control} ${
        props.emailState.isValid === false ? styles.invalid : ""
      }`}
    >
      <label htmlFor={props.forWhat === "email" ? "email" : "password"}>
        E-Mail
      </label>
      <input
        type={props.forWhat === "email" ? "email" : "password"}
        id={props.forWhat === "email" ? "email" : "password"}
        value={props.emailState.value}
        onChange={props.emailChangeHandler}
        onBlur={props.validateEmailHandler}
      />
    </div>
  );
};

export default Input;
